mapper.select:
SELECT
,staff.name as userName
FROM b_department a
left join b_account act on act.id = a.user_id
left join b_staff staff on staff.id = act.staff_id


        and a.id=#{ id }


        and a.organization_id=#{ organizationId }


        and a.code LIKE CONCAT('%', #{ code }, '%')


        and a.parent_id=#{ parentId }


        and a.name LIKE CONCAT('%', #{ name }, '%')


        and a.type_id=#{ typeId }


        and a.contact_name LIKE CONCAT('%', #{ contactName }, '%')


        and a.contact_phone LIKE CONCAT('%', #{ contactPhone }, '%')


        and a.user_id=#{ userId }


        and a.status = #{ status }


        and a.created LIKE CONCAT('%', #{ created }, '%')


        and a.remark LIKE CONCAT('%', #{ remark }, '%')


order by a.created desc

SELECT
,staff.name as userName
FROM b_department a
left join b_account act on act.id = a.user_id
left join b_staff staff on staff.id = act.staff_id


        and a.id=#{ id }


        and a.organization_id=#{ organizationId }


        and a.code LIKE CONCAT('%', #{ code }, '%')


        and a.parent_id=#{ parentId }


        and a.name LIKE CONCAT('%', #{ name }, '%')


        and a.type_id=#{ typeId }


        and a.contact_name LIKE CONCAT('%', #{ contactName }, '%')


        and a.contact_phone LIKE CONCAT('%', #{ contactPhone }, '%')


        and a.user_id=#{ userId }


        and a.status = #{ status }


        and a.created LIKE CONCAT('%', #{ created }, '%')


        and a.remark LIKE CONCAT('%', #{ remark }, '%')


limit 1
